import React, { useState } from 'react';
import ActionButtons from '../UI/ActionButtons';

import styles from './InvestForm.module.css';

const initialUserInput = {
  'current-savings': 10000,
  'yearly-contribution': 1200,
  'expected-return': 7,
  duration: 10,
};

const InvestForm = (props) => {
  const [userInput, setUserInput] = useState(initialUserInput);

  const handleSubmit = (e) => {
    e.preventDefault();

    props.onCalculate(userInput);
  };

  const resetHandler = () => {
    setUserInput(initialUserInput);
  };

  return (
    <form className={styles.form} onSubmit={handleSubmit}>
      <div className={styles['input-group']}>
        <p>
          <label htmlFor='current-savings'>Current Savings ($)</label>
          <input
            type='number'
            id='current-savings'
            onChange={(e) =>
              setUserInput((prevState) => ({
                ...prevState,
                'current-savings': e.target.value,
              }))
            }
          />
        </p>
        <p>
          <label htmlFor='yearly-contribution'>Yearly Savings ($)</label>
          <input
            type='number'
            id='yearly-contribution'
            onChange={(e) =>
              setUserInput((prevState) => ({
                ...prevState,
                'yearly-contribution': e.target.value,
              }))
            }
          />
        </p>
      </div>
      <div className={styles['input-group']}>
        <p>
          <label htmlFor='expected-return'>
            Expected Interest (%, per year)
          </label>
          <input
            type='number'
            id='expected-return'
            onChange={(e) =>
              setUserInput((prevState) => ({
                ...prevState,
                'expected-return': e.target.value,
              }))
            }
          />
        </p>
        <p>
          <label htmlFor='duration'>Investment Duration (years)</label>
          <input
            type='number'
            id='duration'
            onChange={(e) =>
              setUserInput((prevState) => ({
                ...prevState,
                duration: e.target.value,
              }))
            }
          />
        </p>
      </div>
      <ActionButtons data={userInput} onCalculate={props.onCalculate} />
    </form>
  );
};

export default InvestForm;
