import React, { useState } from 'react';
import ActionButtons from '../UI/ActionButtons';

import styles from './InvestForm.module.css';

const initialUserInput = {
  'current-savings': 0,
  'yearly-contribution': 0,
  'expected-return': 0,
  duration: 0,
};

const InvestForm = (props) => {
  const [userInput, setUserInput] = useState(initialUserInput);
  return (
    <form className={styles.form}>
      <div className={styles['input-group']}>
        <p>
          <label htmlFor='current-savings'>Current Savings ($)</label>
          <input
            type='number'
            id='current-savings'
            onChange={(e) =>
              setUserInput((prevState) => ({
                ...prevState,
                'current-savings': e.target.value,
              }))
            }
          />
        </p>
        <p>
          <label htmlFor='yearly-contribution'>Yearly Savings ($)</label>
          <input
            type='number'
            id='yearly-contribution'
            onChange={(e) =>
              setUserInput((prevState) => ({
                ...prevState,
                'yearly-contribution': e.target.value,
              }))
            }
          />
        </p>
      </div>
      <div className={styles['input-group']}>
        <p>
          <label htmlFor='expected-return'>
            Expected Interest (%, per year)
          </label>
          <input
            type='number'
            id='expected-return'
            onChange={(e) =>
              setUserInput((prevState) => ({
                ...prevState,
                'expected-return': e.target.value,
              }))
            }
          />
        </p>
        <p>
          <label htmlFor='duration'>Investment Duration (years)</label>
          <input
            type='number'
            id='duration'
            onChange={(e) =>
              setUserInput((prevState) => ({
                ...prevState,
                duration: e.target.value,
              }))
            }
          />
        </p>
      </div>
      <ActionButtons data={userInput} onCalculate={handleCalculate} />
    </form>
  );
};

export default InvestForm;
